def find_position
	puts "\nTu veux trouver la position de quelqu'un dans l'array?
Tape son handle si dessous:"
	print "> "
	handle = gets.chomp.to_s
		if handle.include? "@"
			position = list.index("#{handle}")
			if position == nil
				puts "\n
La personne n'est pas dans la liste.
\n"
				find_position
			else
				puts "\n
La personne est en position #{position}.
\n"
			end
		else
			puts "\n
Pensé à tapper @ avant le handle de la personne.
\n"
			find_position
		end
end

def boucleorend
	puts "\nVeux-tu en rester la?
Appuye Y pour quitter / Appuye N pour continuer.
\n"
	print "> "
	@choice = gets.chomp.to_s

	while @choice != "Y" && @choice !="N"

		puts "\n
Je n'ai pas compris,
Appuye Y pour quitter / Appuye N pour continuer"

		print "> "
		@choice = gets.chomp
	end

		if @choice == "N"
			menu

		elsif @choice == "Y"
			
			puts "A la prochaine!"
		end
end

def menu 
	puts "
-----------------------------------------------------------------------------
\n
	Bonjour, tu es dans un array de handle de journalites sur Twitter.
Que souhaite tu faires?
\n
-----------------------------------------------------------------------------
\n
Tape 1 pour voir la liste entière des handles.
Tape 2 pour savoir combiens il y a de journaliste dans l'array.
Tape 3 pour savoir combien d'handle contiennent un numéro.
Tape 4 pour savoir combien d'handle contiennent les 4 lettres du prénom Aude.
Tape 5 pour savoir combien possède de majuscule à la première lettre aprés @.
Tape 6 pour savoir combien il y a d'underscore au total.
Tape 7 pour trier l'array par ordre alphabétique.
Tape 8 pour savoir les 50 handle les plus courts.
Tape 9 pour savoir la position d'un handle dans l'array"
		print "> "
		choise = gets.chomp.to_i

	while (choise < 1 && choise > 10)
		puts "Je n'ai pas compris ce que tu voulais faire :
Tape 1 pour voir la liste entière des handles.
Tape 2 pour savoir combiens il y a de journaliste dans l'array.
Tape 3 pour savoir combien d'handle contiennent un numéro.
Tape 4 pour savoir combien d'handle contiennent les 4 lettres du prénom Aude.
Tape 5 pour savoir combien possède de majuscule à la première lettre aprés @.
Tape 6 pour savoir combien il y a d'underscore au total.
Tape 7 pour trier l'array par ordre alphabétique.
Tape 8 pour savoir les 50 handle les plus courts.
Tape 9 pour savoir la position d'un handle dans l'array"
		print "> "
		choise = gets.chomp
	end

	if choise == 1
		puts "Voici la liste des journalistes de tout l'array:
		\n
	#{list}.
	\n"

	elsif choise == 2
		puts "\n Il y a #{all_list} journalistes dans l'array.
		\n"

	elsif choise == 3
		puts "\n Il y a #{num_inlist} handles qui contiennent un ou des numéro-s.
		\n"

	elsif choise == 4
		puts "\n Il y a #{aude_inlist} handles qui contiennent les 4 lettres du prénom Aude à la suite.
		\n"

	elsif choise == 5
		puts "\nIl y a #{nb_beginmaj_inlist} handles qui commencent avec une majuscule aprés @.
		\n"

	elsif choise == 6
		puts "\nIl y a #{nb_underscore_list} underscore au total dans tout l'array.
		\n"

	elsif choise == 7
		puts "\nVoici l'array trier par ordre alphabetique:
#{sort_list}
\n"

	elsif choise == 8
		puts "\nVoici la liste des 50 handles les plus court de l'array:
#{min50_list}
\n"

	elsif choise == 9
		find_position
	end

	boucleorend
end



